sysauto_script.script:
//Query the database for NeedIt Task records  with Due date field values older than the current time. 
//Only return NeedIt Task records that do not have  a state field value of Closed complete
var TODAY = new GlideDateTime();
var OVERDUE_NI_TASKS = new GlideRecord('x_58872_needit_needit_task');
OVERDUE_NI_TASKS.addQuery('due_date','<=',TODAY);
OVERDUE_NI_TASKS.addQuery('state','!=',3);
OVERDUE_NI_TASKS.query();
//Write a log message for each overdue  NeedIt Task record
while(OVERDUE_NI_TASKS.next()){	gs.eventQueue('x_58872_needit.overdueNeedItTasks',OVERDUE_NI_TASKS,OVERDUE_NI_TASKS.number,gs.getUserName());
    gs.info("Overdue NeedIt Task record: " + OVERDUE_NI_TASKS.number);
}
